jQuery.validator.setDefaults({
    errorElement: 'span', //默认错误容器
    errorClass: 'form-control-feedback', // 默认错误提示
    focusInvalid: false, // 不自动将焦点放到最后的无效输入框
    ignore: '',  // 验证包括隐藏元素在内的所有输入框

    errorPlacement: function(error, element) { // render error placement for each input type
        var group = $(element).closest('.form-group').length > 0 ? $(element).closest('.form-group') : $(element).closest('.m-form__group');
        var help = group.find('.m-form__help, .m-radio-inline, .m-checkbox-inline');

        if (group.find('.form-control-feedback').length !== 0) {
            return;
        }

        if (help.length > 0) {
            help.before(error);
        } else {
            if ($(element).closest('.input-group').length > 0) {
                $(element).closest('.input-group').after(error);
            } else {
                if ($(element).is(':checkbox')) {
                    $(element).closest('.m-checkbox').find('>span').after(error);
                } else {
                    $(element).after(error);
                }                
            }            
        }
    },

    highlight: function(element) { // 高亮未通过验证的输入框
        var group = $(element).closest('.form-group').length > 0  ? $(element).closest('.form-group') : $(element).closest('.m-form__group');
        group.addClass('has-danger');
    },

    unhighlight: function(element) { // 取消高亮
        var group = $(element).closest('.form-group').length > 0  ? $(element).closest('.form-group') : $(element).closest('.m-form__group');
        if(group.find('.form-control-feedback').length === 0){
            group.removeClass('has-danger');
        }
    },

    success: function(label, element) {
        var group = $(label).closest('.form-group').length > 0  ? $(label).closest('.form-group') : $(label).closest('.m-form__group');

        //group.addClass('has-success'); // 设置验证通过class
        group.removeClass('has-danger'); // 移除验证失败class
        group.find('.form-control-feedback').remove();
    }
});

